import CounterInput from 'react-bootstrap-counter';
import Travel from './travel.js';
import PersonContext from './personcontext';

class Search extends Component {

    state = { 
        isVisible  : false
    
    
    }

    onClickEvent = (e)=>{

this.setState({
    isVisible : !this.state.isVisible
})
   }
    render() {
        const personContext = useContext(PersonContext);
        const { personid } = personContext;


        const {isVisible} = this.state;
        return (
            <div className='h-100'>

<a href='#' onClick = {this.onClickEvent}> <button href='/search.html'id="srch"  className="rounded-pill search-button border-0 rs-img-hidden color-white ">Ara</button> </a>
{
isVisible ?

<div className="background-blur">
   <div className="container p-0 search-menu text-white ">
   <span className='badge text-bg-danger f-right'>   <i type="button" className="fas fa-times f-right p-1" onClick = {this.onClickEvent}></i> </span>
       <div className="container p-4">
 
       <div className=" col-md-12 text-dark"><span>Aramanız Şu Şekilde : <br/>1 oda {personid} Yetişkin {childid} Çocuk </span></div>
       </div>
   </div>
   </div>
   
: null
}               
            </div>
        )
    }
}

export default Search ;
