import React, { Component,useState,useRef,useEffect } from 'react'
import App from '../App'
import Body from './body';
import Search from './search';
import Travel from './travel';
import scriptLoader from 'react-async-script-loader';
import PlacesAutocomplete from 'react-places-autocomplete';


export default function Srcinput( ) {
 
    const [city, setCity] = useState('');

    const handleChange = (event) => {
       
        setCity(event.target.value);
      };
     
        const autoCompleteRef = useRef();
        const inputRef = useRef();
        const options = {
         componentRestrictions: { country: "ng" },
         fields: ["address_components", "geometry", "icon", "name"],
         types: ["establishment"]
        };
        useEffect(() => {
         autoCompleteRef.current = new window.google.maps.places.Autocomplete(
          inputRef.current,
          options
         );
        }, []);



      return (

        <div className="col-xxl-4  p-0 border-end border-3 border-dark pill-border pill-border-bottom "><i class="fas fa-bed fa-lg p-4 "></i> 
   
<input ref={inputRef}  id='adres' onChange={handleChange} autocomplete="on" className="border border-0 h-100 pill-input" type="text" placeholder="Gitmek istediğiniz yer ?"  />
            </div>
      )   
}
