import React, { useState, useRef } from 'react';
import { Fragment } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function DatePickercomp() {
  const today = new Date();
  const tomorrow = new Date();
  tomorrow.setDate(tomorrow.getDate() + 1);
  const maxday = new Date();
  maxday.setDate(maxday.getDate() + 30);
  const [departureDate, setDepartureDate] = useState(null);
  const [returnDate, setReturnDate] = useState(null);
  const returnDateRef = useRef(null);

  const handleDepartureDateChange = (date) => {
    setDepartureDate(date);
    setReturnDate(null); 
    returnDateRef.current.setFocus(); 
  };

  const handleReturnDateChange = (date) => {
    setReturnDate(date);
  };

  return (

  <div className="col-xxl-4 p-0  m-auto  pill-border-bottom">
    <div className="row m-auto" >
 <div className="col-6 m-auto"><i class="fa fa-calendar p-2 responsive-padding" aria-hidden="true"></i>  
   <strong className="body-title-color h6 p-1 responsive-padding"   > Giriş - Çıkış Tarihi</strong> </div>

                <div className="col-6 m-auto responsive-padding">

      <DatePicker
        selected={departureDate}
        onChange={handleDepartureDateChange}
        placeholderText="Gidiş Tarihi"
        minDate={today}
        maxDate={maxday} 
        className='datepickercustom-go'

      />

      <DatePicker
        selected={returnDate}
        onChange={handleReturnDateChange}
        placeholderText="Dönüş Tarihi"
        minDate={departureDate || tomorrow} 
        maxDate={maxday}
        ref={returnDateRef}
        className='datepickercustom-rtrn'
      />
      {/*   Gidiş Tarihi valuesunu almak için {departureDate ? departureDate.toLocaleDateString('tr-TR') : ''}</p>
      <p>Dönüş Tarihi valuesunu almak için  {returnDate ? returnDate.toLocaleDateString('tr-TR') : ''}</p>    */}



    </div>
                </div>
         </div>








  );
}

export default DatePickercomp;
