            <div className='h-100'>

<a href='#' onClick = {this.onClickEvent}> <button href='/search.html'id="srch"  className="rounded-pill search-button border-0 rs-img-hidden color-white ">Ara</button> </a>
{
isVisible ?

<div className="background-blur">
   <div className="container p-0 search-menu text-white ">
