       <div className="container p-4">
 
       <div className=" col-md-12 text-dark"><span>Aramanız Şu Şekilde : <br/>1 oda {this.props.personid} Yetişkin {this.props.childid} Çocuk </span></div>
       </div>
   </div>
   </div>
   
: null
}               
            </div>
        )
    
}
