import React, { useState } from 'react';
import App from '../App'
import '../App.css';
import { ReactDOM } from 'react-dom/client';
import CounterInput from 'react-bootstrap-counter';





export default function Travel() {






        var [personid,setPersonid] =useState(0);
        var [childid,setChildid] =useState(0);
        if (personid>5){
            personid = 5  
             }
        else if ( personid<0){
            personid = 0
        }
        if (childid>5){
            childid = 5  
             }
        else if ( childid<0){
            childid = 0
        }

        return (

            <div className="position-absolute travel-comp container">

     <div className="row p-2 responsive-padding">
    <div className="col-md-12 p-3 responsive-padding">  <i class="far fa-user p-2 responsive-padding rs-img-hidden"></i> <span className="rs-img-hidden"> 1 Oda, 2 Yetişkin, 3 Çocuk </span>  </div>
    <div className="col-md-12 p-3 responsive-padding rs-img-hidden"><strong>1. oda için kişi sayısı</strong></div>
    <div className="col p-3 responsive-padding"> <div className="row">
    <div className="col-md-4 m-auto text-center"><span>Yetişkin</span></div>
    <div className="col-md-8 m-auto text-center input-mouse-event  ">

      <div className='container'>

        <button onClick={()=>{setPersonid(personid-1)}} className='col-3 bg-white downbutton '>-</button>
        <input className='col-6 rounded-pill text-center' type='text' value={personid}></input>
        <button onClick={()=>{setPersonid(personid+1)}} className='col-3 bg-white upbutton ' >+</button>
        </div>

        </div> 

    </div> 


    </div>
    <div className="col-md-12 hidden-items"></div>
    <div className="col p-3 responsive-padding "> <div className="row">
    <div className="col-md-4 m-auto text-center"><span>Çocuk</span></div>
    <div className="col-md-8 m-auto text-center input-mouse-event ">

    <div className='container'>
       <button onClick={()=>{setChildid(childid-1)}} className='col-3 bg-white downbutton'  name='-'>-</button>
        <input className='col-6 rounded-pill text-center' type='text' value={childid} ></input>
        <button onClick={()=>{setChildid(childid+1)}} className='col-3 bg-white upbutton '  name='+'>+</button>
        </div>

   </div>
    </div> 
    </div>
    <div className="col-md-12 p-3"> <a href="#" className="text-decoration-none hidden-items">Oda Ekle</a> </div>
    <div className="col-md-12 p-3 d-none rs-img-block"> <a className="btn btn-danger w-100" href="/search.html">Ara</a></div>
    </div>



            </div>
        )
    }
