import React, { Component,useState,useRef,useEffect } from 'react'
import App from '../App'
import Body from './body';
import Search from './search';
import Travel from './travel';
import scriptLoader from 'react-async-script-loader';
import PlacesAutocomplete from 'react-places-autocomplete';
import Roundtrip from './roundtrip';

export default function Srcinput( ) {
 
    const [city, setCity] = useState('');
    const cityinput = document.getElementById("adress");

     
        const autoCompleteRef = useRef(null);
        const inputRef = useRef(null);
        const options = {
         componentRestrictions: { country: "tr" },
         fields: ["geometry", "name","address_components"],
         types: ["geocode"]
        };
        useEffect(() => {
          const autoComplete = new window.google.maps.places.Autocomplete(inputRef.current, options);
          autoCompleteRef.current = autoComplete;
          autoComplete.addListener('place_changed', onPlaceChanged);

       }, []);
       const onPlaceChanged = () => {
        const place = autoCompleteRef.current.getPlace();
        const selectedCity = place.formatted_address;
        setCity(selectedCity);
      };


      return (
        <div className="row border border-3 border-dark rounded-pill pill-comp p-0 bg-white m-auto h-100">
        <div className="col-xxl-4 h-100  p-3 border-end border-3 border-dark pill-border pill-border-bottom m-auto"> <div className='row'>
      <div className='col-xxl-2'>    <i class="fas fa-bed fa-lg p-4 "></i> </div>
      <div className='col-xxl-10'>  
<input  ref={inputRef}  id='adress' value={city}  autocomplete="on" className="border border-0 h-100 pill-input w-100" type="text" placeholder="Gitmek istediğiniz yer ?"  />

{city}
</div></div>  
</div>
<Roundtrip city= {city}/>

</div>
      )   
}
