      if (syc==0){
     
        document.getElementById("hlp").style.display="block";
        syc =syc+1;
 }
 else if (syc ==1){

    document.getElementById("hlp").style.display="none";
    syc = syc-1;
 }


       }
       

  return (
    <div className="col-2 m-auto navbar-text-white hidden-items itms-h" >
    
    
        <a type="button" onClick={click} value="Yardım"><h6>Yardım</h6></a>

        <div id="hlp" >
        
        <div className="background-blur">
   <div className="container p-0 currency-menu text-white">
