import React, { Component, useState } from 'react'
import App from '../App'
import CounterInput from 'react-bootstrap-counter';
function example() {
    const [personid, setPersonid] = useState=(null);



 class Travel extends Component {





    render() {
        return (

            <div className="position-absolute travel-comp container">
      <div className="row p-2 responsive-padding">
    <div className="col-md-12 p-3 responsive-padding">  <i class="far fa-user p-2 responsive-padding rs-img-hidden"></i> <span className="rs-img-hidden"> 1 Oda, 2 Yetişkin, 3 Çocuk </span>  </div>
    <div className="col-md-12 p-3 responsive-padding rs-img-hidden"><strong>1. oda için kişi sayısı</strong></div>
    <div className="col p-3 responsive-padding"> <div className="row">
    <div className="col-md-6 m-auto text-center"><span>Yetişkin</span></div>
    <div className="col-md-6 m-auto text-center input-mouse-event  "><CounterInput max={5} min={1} onChange={ (value) => { let personid } } value={1} /> </div>

    </div> 


    </div>
    <div className="col-md-12 hidden-items"></div>
    <div className="col p-3 responsive-padding "> <div className="row">
    <div className="col-md-6 m-auto text-center"><span>Çocuk</span></div>
    <div className="col-md-6 m-auto text-center input-mouse-event ">
    <CounterInput max={5} min={1} value={1} onChange={ (value ) => { this.setState({personid});   }  }  /> </div>
    </div> 
    </div>
    <div className="col-md-12 p-3"> <a href="#" className="text-decoration-none hidden-items">Oda Ekle</a> </div>
    <div className="col-md-12 p-3 d-none rs-img-block"> <a className="btn btn-danger w-100" href="/search.html">Ara</a></div>
    </div>



            </div>
        )
    }
}
}
export  default Travel;
