        const {personid, childid,city} = this.props;
        const {isVisible } = this.state;
    
        return (
            <div className='h-100 '>

<span type='button' className=' text-center w-100 h-100 align-middle' onClick = {this.onClickEvent}>Ara</span>
{
isVisible ?

<div className="background-blur">
   <div className="container p-0 search-menu text-white ">
   <span className='badge text-bg-danger f-right'>   <i type="button" className="fas fa-times f-right p-1" onClick = {this.onClickEvent}></i> </span>
       <div className="container p-4">
 
       <div className=" col-md-12 text-dark"><span>Aramanız Şu Şekilde : <br/>{city} 1 oda {personid} Yetişkin {childid}  Çocuk  </span></div>
       </div>
   </div>
   </div>
   
: null
}               
            </div>
        )
    }
}

export default Search ;
