import App from '../App'
import Body from './body';
import Search from './search';
import Travel from './travel';
import scriptLoader from 'react-async-script-loader';
import PlacesAutocomplete from 'react-places-autocomplete';


function Srcinput( {isScriptLoaded, isScriptLoadSucceed }) {
 
    const [city, setCity] = useState('');

    const handleChange = (event) => {
       
        setCity(event.target.value);
      };
     
      return (

        <div className="col-xxl-4  p-0 border-end border-3 border-dark pill-border pill-border-bottom "><i class="fas fa-bed fa-lg p-4 "></i> 
   
<input  id='adres' onChange={handleChange} autocomplete="on" className="border border-0 h-100 pill-input" type="text" placeholder="Gitmek istediğiniz yer ?"  />

  

    <script async
    src="https://maps.googleapis.com/maps/api/js?key=AIzaSyCLL0kCF4J973EOz0xwP4okOC0OUIACNJE&libraries=places&callback=activatePlacesSearch">
</script>

            </div>
      )
    
}
export default scriptLoader (['https://maps.googleapis.com/maps/api/js?key=AIzaSyCLL0kCF4J973EOz0xwP4okOC0OUIACNJE&libraries=places'])(Srcinput);
